data.sfn-selectors.yaml:
pods:
    matchExpressions:
        - {key: cloud, operator: In, values: [storage]}
nodes:
    matchExpressions:
        - {key: pool, operator: In, values: ["prd-sam_cephdev", "prd-sam_storage", "prd-sam_sfstoredev"]}
persistentvolumes:
    matchExpressions:
        - {key: pool, operator: In, values: ["prd-sam_cephdev", "prd-sam_storage", "prd-sam_sfstoredev"]}
persistentvolumeclaims:
    matchExpressions:
        - {key: daemon, operator: In, values: ["mon", "osd"]}
statefulsets:
    matchExpressions:
        - {key: daemon, operator: In, values: ["mon", "osd"]}
